import React from 'react'
// import {PythonShell} from 'python-shell';
// const filePath = '../../pythonSh/index.py'

// const {PythonShell} = require( 'python-shell'.PythonShell;
const PythonShell = require('python-shell').PythonShell;

class ConnectPython extends React.Component {
    constructor(props) {
        super(props)
    }

    runTest() {
        // PythonShell.run(filePath, null, function (err) {
        //     if (err) throw err;
        //     console.log('finished');
        //   });

        const fileName = 'index.py'

        var options = {
          mode: 'text',
          pythonPath: '/usr/bin/python3', 
          pythonOptions: ['-u'],
          // make sure you use an absolute path for scriptPath
          scriptPath: '../../pythonSh/',
          args: ['value1', 'value2', 'value3']
        };
        console.log("###")
        PythonShell.run(fileName, options, function (err, results) {
          if (err) throw err;
          // results is an array consisting of messages collected during execution
          console.log('results: %j', results);
        });

    }

    render() {
        return (
            <>
                <button onClick={this.runTest()}>test</button>

            </>
        )
    }
}
export default ConnectPython
